# figure out most frequently used variable names
def config_names(repos_ls):
    vars = []
    for d in repos_ls:
        for e in d["config"]:
            vars.append(e["name"])
    counts = { k: vars.count(k) for k in set(vars) }
    order = sorted(counts.items(), key=lambda _: _[1], reverse=True)
    print(dict([k for k in order if k[1] >= 8 ]))
